import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave } from '@fortawesome/free-solid-svg-icons';
import { useAddNewNoteMutation } from './notesApiSlice';

const NewNoteForm = ({ users }) => {
  //
  const navigate = useNavigate();
  //
  const [addNewNote, { isLoading, isSuccess, isError, error }] =
    useAddNewNoteMutation();
  //
  // state vars
  const [title, setTitle] = useState('');
  const [text, setText] = useState('');
  const [userId, setUserId] = useState(users[0].id);
  //
  useEffect(() => {
    if (isSuccess) {
      setTitle('');
      setText('');
      setUserId('');
      navigate('/dash/notes');
    }
  }, [isSuccess, navigate]);

  const onTitleChanged = e => setTitle(e.target.value);
  const onTextChanged = e => setText(e.target.value);
  const onUserIdChanged = e => setUserId(e.target.value);
  //

  const onSaveNoteClicked = async e => {
    e.preventDefault();
    if (canSave) {
      await addNewNote({ user: userId, title, text });
    }
  };
  // alternative to calling '&' a bunch. All in an Array and use '.every'
  const canSave = [title, text, userId].every(Boolean) && !isLoading;
  //

  //classes that we may or may not want to apply
  const errClass = isError ? 'errmsg' : 'offscreen';
  const validTitleClass = !title ? 'form__input--incomplete' : '';
  const validTextClass = !text ? 'form__input--incomplete' : '';

  const content = (
    <>
      <p className={errClass}>{error?.data?.message}</p>

      <form className="form" onSubmit={onSaveNoteClicked}>
        <div className="form__title-row">
          <h2>New Note</h2>
          <div className="form__action-buttons">
            <button className="icon-button" title="Save" disabled={!canSave}>
              <FontAwesomeIcon icon={faSave} />
            </button>
          </div>
        </div>
        <label className="form__label" htmlFor="title">
          Title: <span className="nowrap">[3-50 letters]</span>
        </label>
        <input
          className={`form__input ${validTitleClass}`}
          id="title"
          name="title"
          type="text"
          autoComplete="off"
          value={title}
          onChange={onTitleChanged}
        />

        <label className="form__label" htmlFor="text">
          Text: <span className="nowrap"></span>
        </label>
        <input
          className={`form__input ${validTextClass}`}
          id="text"
          name="text"
          type="textarea"
          value={text}
          onChange={onTextChanged}
        />
        <label
          className="form__label form__checkbox-container"
          htmlFor="completed"
        >
          Completed:
          <input
            className="form__checkbox"
            id="completed"
            name="completed"
            type="checkbox"
            checked={completed}
            onChange={onCompletedChanged}
          />
        </label>
      </form>
    </>
  );

  return content;
};

export default NewNoteForm;
