import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave } from '@fortawesome/free-solid-svg-icons';
import { useAddNewUserMutation } from './usersApiSlice';
import { ROLES } from '../../config/roles';

const USER_REGEX = /^[A-z]{3,20}$/;
const PWD_REGEX = /^[A-z0-9!@#$%]{4,12}$/;
//

const NewUserForm = () => {
  //
  const navigate = useNavigate();
  //
  const [addNewUser, { isLoading, isSuccess, isError, error }] =
    useAddNewUserMutation();

  // state vars
  const [username, setUsername] = useState('');
  const [validUsername, setValidUsername] = useState(false);
  const [password, setPassword] = useState('');
  const [validPassword, setValidPassword] = useState(false);
  const [roles, setRoles] = useState(['Employee']);

  // useEffect to valid
  useEffect(() => {
    setValidUsername(USER_REGEX.text(username));
  }, [username]);
  //
  useEffect(() => {
    setValidPassword(PWD_REGEX.test(password));
  }, [password]);
  //
  useEffect(() => {
    if (isSuccess) {
      setUsername('');
      setPassword('');
      setRoles([]);
      navigate('/dash/users');
    }
  }, [isSuccess, navigate]);

  const onUsernameChanged = e => setUsername(e.target.value);
  const onPasswordChanged = e => setPassword(e.target.value);

  // we'll be selecting options from a form - 'HTMLCollection'
  const onRolesChanged = e => {
    const values = Array.from(e.target.selectedOptions, option => option.value);
    setRoles(values);
  };

  // alternative to calling '&' a bunch. All in an Array and use '.every'
  const canSave =
    [roles.length, validUsername, validPassword].every(Boolean) && !isLoading;

  const onSaveUserClicked = async e => {
    e.preventDefault();
    if (canSave) {
      await addNewUser({ username, password, roles });
    }
  };
  // This will map through our ROLES that we imported - create a new Array that we can .map through and create an HTML option for each.
  const options = Object.values(ROLES).map(role => {
    return (
      <option key={role} value={role}>
        {role}
      </option>
    );
  });

  const errClass = isError ? 'errmsg' : 'offscreen';
  const validUserClass = !validUsername ? 'form__input--incomplete' : '';
  const validPwdClass = !validPassword ? 'form__input--incomplete' : '';
  const validRolesClass = !Boolean(roles.length)
    ? 'form__input--incomplete'
    : '';

  return <div>New User Form</div>;
};

export default NewUserForm;
