import { useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
  faRightFromBracket,
  faFileCirclePlus,
  faFilePen,
  faUserGear,
  faUserPlus,
} from '@fortawesome/free-solid-svg-icons'
import { useNavigate, Link, useLocation } from 'react-router-dom'
//
import { useSendLogoutMutation } from '../features/auth/authApiSlice'
//
import useAuth from '../hooks/useAuth'
//
const DASH_REGEX = /^\/dash(\/)?$/
const NOTES_REGEX = /^\dash\/notes(\/)?$/
const USERS_REGEX = /^\dash\/users(\/)?$/

const DashHeader = () => {
  //
  const { isAdmin, isManager } = useAuth()
  // Navigate and DESTRUCTURE 'pathname' from useLocation()
  const navigate = useNavigate()
  const { pathname } = useLocation()
  //
  const [sendLogout, { isLoading, isSuccess, isError, error }] =
    useSendLogoutMutation()

  // CHECK USESUCCESS - if so navigate to the '/' ------------------------------
  useEffect(() => {
    if (isSuccess) navigate('/')
  }, [isSuccess, navigate])
  //
  const onNewNoteClicked = () => navigate('/dash/notes/new')
  const onNewUserClicked = () => navigate('/dash/users/new')
  const onNotesClicked = () => navigate('/dash/notes')
  const onUsersClicked = () => navigate('/dash/users')

  //
  const onLogoutClicked = () => sendLogout()
  //
  if (isLoading) return <p>Loading...</p>
  //
  if (isError) return <p>Error: {error.data?.message}</p>
  //
  let dashClass = null
  if (
    !DASH_REGEX.test(pathname) &&
    !NOTES_REGEX.test(pathname) &&
    !USERS_REGEX.test(pathname)
  ) {
    dashClass = 'dash-header__container--small'
  }

  // NEW NOTES BUTTON --------------------------------------------------
  let newNoteButton = null
  if (NOTES_REGEX.test(pathname)) {
    newNoteButton = (
      <button
        className="icon-button"
        title="New Note"
        onClick={onNewNoteClicked}
      >
        <FontAwesomeIcon icon={faFileCirclePlus} />
      </button>
    )
  }
  // NOTES CLICKED BUTTON ------------------------------------------------------------
  let notesClickedButton = null
  if (NOTES_REGEX.test(pathname) && pathname.includes('/dash')) {
    notesClickedButton = (
      <button className="icon-button" title="Notes" onClick={onNotesClicked}>
        <FontAwesomeIcon icon={faFilePen} />
      </button>
    )
  }
  // NEW USERS BUTTON ------------------------------------------------------------
  let newUserButton = null
  if (USERS_REGEX.test(pathname)) {
    newUserButton = (
      <button
        className="icon-button"
        title="New User"
        onClick={onNewUserClicked}
      >
        <FontAwesomeIcon icon={faUserPlus} />
      </button>
    )
  }
  // USER CLICKED BUTTON ------------------------------------------------------------
  let userButton = null
  if (isManager || isAdmin) {
    if (USERS_REGEX.test(pathname) && pathname.includes('/dash')) {
      userButton = (
        <button className="icon-button" title="Users" onClick={onUsersClicked}>
          <FontAwesomeIcon icon={faUserGear} />
        </button>
      )
    }
  }
  // LOGOUT BUTTON --------------------------------------------------------------
  const logoutButton = (
    <button className="icon-button" title="Logout" onClick={onLogoutClicked}>
      <FontAwesomeIcon icon={faRightFromBracket} />
    </button>
  )

  const errClass = isError ? 'errmsg' : 'offscreen'

  // CONTENT VAR ----------------------------------------------------------------
  const content = (
    <header className="dash-header">
      <div className={`dash-header__container ${dashClass}`}>
        <Link to="/dash">
          <h1 className="dash-header__title">techNotes</h1>
        </Link>
        <nav className="dash-header__nav">
          {/* ADD MORE BUTTONS LATER */}
          {logoutButton}
        </nav>
      </div>
    </header>
  )

  return content
}

export default DashHeader
